/* get 함수 : list의 n번째에 저장되어 있는 값을 리턴한다. */
template <typename T>
T unvlist :: get (int n){
	T retval;

	if (n >= len || n < 0){
		cout << "'In unvlist::get'-> Out of Bound!!!" << endl;
		return 0;
	}
	if (n >= 0){
		node_base *h = head;
		for (int i = 0; i < n; i++) { h = h->next; }
		retval = static_cast<node<T>*>(h)->getData ();
	}
	return retval;
}
