    for(int b=1; b<board.size();b++){
      if(tem[a][b]==1){
      	tem[a][b] = Min(tem[a-1][b-1],tem[a-1][b],tem[a][b-1])+1;
        max = max > tem[a][b] ? max : tem[a][b];
      }
    }
  }
	return pow(max,2);
}

