void Initialize()
{
	head = (Student *)malloc(sizeof(Student));//더미 노드 생성
	tail = (Student *)malloc(sizeof(Student));//더미 노드 생성
	head->next = tail;//맨 앞 더미노드 next를 tail로 설정
	tail->prev = head;//맨 뒤 더미노드 prev를 head로 설정
	head->prev = tail->next = NULL;
}

