typedef struct _Stack
{
    int arr[STACK_LEN];
    int topIndex;
}  ST;

void SstackInit(ST *pstack)
{
    pstack->topIndex = -1;
}

void Spush(ST *pstack, int X)
{
    pstack->topIndex += 1;
    pstack->arr[pstack->topIndex] = X;
}

int Sempty(ST *pstack)
{
    if (pstack->topIndex == -1)
        return TRUE;
    else
        return FALSE;
}

int Spop(ST *pstack)
{
    int imsi;

    if (Sempty(pstack))
    {
        printf("error!");
        return -1;
    }

    imsi = pstack->topIndex;
    pstack->topIndex -= 1;
    return pstack->arr[imsi];

}   

int Ssize(ST *pstack)
{   
    return pstack->topIndex + 1;     //-1부터 시작이니깐

}

int Stop(ST *pstack)
{
    if (Sempty(pstack))
    {
        return -1;
    }

    return pstack->arr[pstack->topIndex];
}


