		scanf("%d", &ec[i]);
		for(j=0;j<ec[i];j++){ //각각의 케이스 수만큼 학생점수 입력 
			scanf("%d", &std[j]);
			avg+=std[j];
		}
		avg/=ec[i];
		for(j=0;j<ec[i];j++){ //평균 넘는 애
			if(std[j]>avg){
				avgover+=1;
			}
		}
		printf("%.3lf", (avgover/(float)ec[i])*100);
	}
	return 0;
}
