				if (col[i] > col[j]) {
					tempRow = row[i]; row[i] = row[j]; row[j] = tempRow;
					tempCol = col[i]; col[i] = col[j]; col[j] = tempCol;
					tempValue = value[i]; value[i] = value[j]; value[j] = tempValue;
				}
			}
		}
