void ReverseArray(int x[], int n)

{

	int i;
	int tmp;
	for (i = 0; i < n; i++)	
	{
		tmp = x[i];
		x[i] = x[n - i - 1];
	}
}

void PrintIntArray(int v[], int n)

{

	int i;
	for (i = n - 1; i >= 0; i--)
		printf("%d", v[i]);
}

