int solution(int n) {
	if (n == 1) return 1;
	if (n == 2) return 2;

	unsigned fm3;
	unsigned fm2 = 0;
	unsigned fm1 = 1;
	unsigned f = 2;

	int ans;
	for (int i = 3; i <= n; i++) {
		fm3 = fm2;
		fm2 = fm1;
		fm1 = f;

		f = (3 * fm1)%= 100000007 + fm2 - fm3;
		ans = f % 100000007;

		if (fm2 >= 100000007 && fm1 >= 100000007 && f >= 100000007) {
			fm2 %= 100000007;
			fm1 %= 100000007;
			f %= 100000007;
		}

	}

	return ans;
}

