typedef struct ListNode {
	int data;
	struct ListNode* link;
}list;

typedef struct {
	list* head;
}list_h;

list_h* create() {
	list_h* L;
	L = (list_h*)malloc(sizeof(list_h*));
	L->head = NULL;
	return L;
}

void addLastNode(list_h* L, int x) {
	list* newNode = (list*)malloc(sizeof(list*));
	list* p;
	newNode->data = x;
	newNode->link = NULL;
	if (L->head == NULL) {
		L->head = newNode;
		return;
	}
	else {
		p = L->head;
		while (p->link != NULL) {
			p = p->link;
		}
		p->link = newNode;
	}
}

void freeList(list_h* L) {
	list* c;
	while (L->head != NULL) {
		c = L->head;
		L->head = L->head->link;
		free(c);
		c = NULL;
	}
}

void reverse(list_h* L) { //L->r->q->p
	list* p = L->head; 
	list* q = NULL; 
	list* r = NULL; 

	while (p != NULL) {
		r = q;
		q = p;
		p = p->link;
		q->link = r;
	}
	L->head = q;
}

void delLastNode(list_h* L) {
	list* a = L->head->link;
	list* b = L->head;
	if (L->head == NULL) return;
	if (L->head->link == NULL) {
		free(L->head);
		L->head = NULL;
		return;
	}
	else {
		while (a->link != NULL) {
			b = a;
			a = a->link;
		}
		free(a);
		b->link = NULL;
	}
}
void printList(list_h* L) {
	list* p = L->head;
	printf("L -> ");
	while (p != NULL) {
		printf("%d ", p->data);
		p = p->link;
		if (p != NULL) printf("-> ");
	}
}
