                        if (p1->order == p2->order)
                        {
                                item->coefficient = p1->coefficient + p2->coefficient;
                                item->order = p1->order;
                                p1 = p1->link;
                                p2 = p2->link;
                        }

                        else
                        {
                                if (p1->order > p2->order)
                                {
                                        item->coefficient = p1->coefficient;
                                        item->order = p1->order;
                                        p1 = p1->link;
                                }

                                if (p1->order < p2->order)
                                {
                                        item->coefficient = p2->coefficient;
                                        item->order = p2->order;
                                        p2 = p2->link;
                                }
                        }
                }

                else
                {
                        if (p1 == NULL)
                        {
                                item->coefficient = p2->coefficient;
                                item->order = p2->order;
                                p1 = p1->link;
                        }

                        if (p2 == NULL)
                        {
                                item->coefficient = p1->coefficient;
                                item->order = p1->order;
                                p2 = p2->link;
                        }
                }

                if (p1 == NULL && p2 == NULL)
                {
                        tail->link = NULL;
                        break;
                }

                if (tail == NULL)
                {
                        head = item;
                }
                else
                {
                        tail->link = item;
                }
                tail = item;
        }

        return head;
}

