'''코드'''

#define TRUE    1
#define FALSE   0

#define MAX     1000000000

int is_prime(int n) {

    if (n <= 1) {       
        return FALSE;
    } else if (n == 2) {
        return TRUE;
    } else {
        for (int x = 2; x <= n-1; x++) {
            if (n % x == 0) {
                return FALSE;
            }
        }
        return TRUE;
    }
}

int main() {

    int printed = 0;
    for (int number = 0; number <= MAX; number++) {
        if (is_prime(number)) {
            if (printed++ % 15 == 0) {
                printf("\n");
            }
            printf("%d ", number);
        }
    }
    printf("\n");
    return 0;
}

