private void cleanUpSocketVariable(Socket sock, InputStream in, OutputStream out) {
        if (in != null) {
                try {
                        in.close();
                } catch (IOException e) {
                        // nothing to do.
                }
                in = null;
        }

        if (out != null) {
                try {
                        out.close();
                } catch (IOException e) {
                        // nothing to do.
                }
                out = null;
        }

        if (sock != null) {
                try {
                        sock.close();
                } catch (IOException e) {
                        // nothing to do.
                }
                sock = null;
        }
}
