    static String IntoPost(StringTokenizer st) 
    {
      GStack<String> stringStack = new GStack<String>();

      int n=st.countTokens(); 

      ArrayList<String> a = new ArrayList<String>(n); 
      for(int i=0;i<n;i++) 
         {   
            String token=st.nextToken();
            try {
               Double.parseDouble(token);
