#include <iostream>
#include <stdlib.h>
	using namespace std;

	class LinkedList {
		struct Node {
			int x;
			Node *next;
		};
	public:
		LinkedList() {
			head = NULL; 
		}
		void addValue(int val) {
			Node *n = new Node();   
			n->x = val;            
			n->next = head;        

			head = n;               
		}
	private:
		Node *head;
	};

	int main() {
		LinkedList list;
		cout << " 입력할 LIst의 개수는?";
		int n;
		cin >> n;
		if (n <= 0) return 0;
		int *p = new int[n];
		for (int i = 0; i < n; i++) {
			list.addValue(p[i]);
		}
	}
