class GStack<T> {

        int top;
        Object[] stck;

        public GStack() {
                top = 0;
                stck = new Object[100];
        }

        public void push(T item) {
                if (top == 100)
                        return;
                stck[top] = item;
                top++;
        }

        public T pop() {
                if (top == 0)
                        return null;
                return (T) stck[top--];
        }

        public T peek() {
                if (top == 0)
                        return null;
                return (T)stck[top];
        }

}
