#include <stdio.h>
#include <stdlib.h>
#include <malloc.h>

typedef struct _list_node
{
struct _list_node *link;
int coefficient;
int order;
} NODE;

static NODE *polyAdd(NODE *head1, NODE *head2)
{
NODE *item = NULL;
NODE *p1 = head1;
NODE *p2 = head2;
NODE *head, *tail = NULL;

while (1)
{
if (p1 != NULL && p2 != NULL)
{
if (p1->order == p2->order)
{
item->coefficient = p1->coefficient + p2->coefficient; /* 여기서 오류*/
item->order = p1->order;
p1 = p1->link;
p2 = p2->link;
}

else
{
if (p1->order > p2->order)
{
item->coefficient = p1->coefficient;
item->order = p1->order;
p1 = p1->link;
}

if (p1->order < p2->order)
{
item->coefficient = p2->coefficient;
item->order = p2->order;
p2 = p2->link;
}
}
}

else
{
if (p1 == NULL)
{
item->coefficient = p2->coefficient;
item->order = p2->order;
p1 = p1->link;
}

if (p2 == NULL)
{
item->coefficient = p1->coefficient;
item->order = p1->order;
p2 = p2->link;
}
}

if (p1 == NULL && p2 == NULL)
{
tail->link = NULL;
break;
}

if (tail = NULL)
{
head = item;
}
else
{
tail->link = item;
}
tail = item;
}

return head;
}

static NODE *create(int *list, int size)
{
int  i;
NODE *item, *head = (NODE *)0;

for (i = 0; i < size; i++) {
if (list[i]) {
item = (NODE *)malloc(sizeof(NODE));
if (!item) {
fprintf(stderr, "\nOut of memory.\n");
exit(1);
}
item->coefficient = list[i];
item->order = i;
item->link = head;
head = item;
}
}
return(head);
}

static int getSize(int *list)
{
int size = 0;
if (!list)
return(0);
while (list[size] >= 0)
size++;
return(size);
}

static void printList(NODE *head)
{
NODE *item = head;
while (item) {
if (item != head)
printf(" +");
if (item->order)
printf(" %d e%d", item->coefficient, item->order);
else
printf(" %d", item->coefficient);
item = item->link;
}
printf("\n");
}

static void deleteList(NODE **head)
{
NODE *del, *item;
if (!head)
{
fprintf(stderr, "[deleteList] usage of function is wrong.\n");
exit(1);
}
item = *head;
while (item)
{
del = item;
item = item->link;
free(del);
}
*head = (NODE *)0;
}


