		Nptr temp = Head;
		Nptr temp2 = Head;
		temp2 = temp2->Next;
		for (int i = 0; i < Count; i++)
		{
			temp = temp->Next;
			for (int j = 0; j < Count-1; j++)
			{
				temp2 = temp2->Next;
				if (temp->Data == temp2->Data) {
					Delete(j+2);
					Count -= 1;
				}
			}
		}
	}
