void remove_node(NODE **head, NODE *p, NODE *removed)
{
	if (p == NULL)
	{
		*head = (*head)->next;
	}
	else
	{
		p->next = removed->next;
	}

	free(removed);
}

void add_node(NODE **head, NODE *p, NODE *new_node)
{
	if (*head == NULL)
	{
		new_node->next = NULL;
		*head = new_node;
	}
	else if (p == NULL)
	{
		new_node->next = *head;
		*head = new_node;
	}
	else
	{
		new_node->next = p->next;
		p->next = new_node;
