v8::Local<v8::Context> CreateShellContext(v8::Isolate* isolate) {
    // Create a template for the global object.
    v8::Local<v8::ObjectTemplate> global = v8::ObjectTemplate::New(isolate);

    // Bind the global 'print' function to the $$$ Print callback.
    global->Set(
        v8::String::NewFromUtf8(isolate, "print", v8::NewStringType::kNormal)
        .ToLocalChecked(),
        v8::FunctionTemplate::New(isolate, Print));
    // Bind the global 'read' function to the $$$ Read callback.
    global->Set(v8::String::NewFromUtf8(
        isolate, "read", v8::NewStringType::kNormal).ToLocalChecked(),
        v8::FunctionTemplate::New(isolate, Read));
    // Bind the global 'load' function to the $$$ Load callback.
    global->Set(v8::String::NewFromUtf8(
